/** @jsx jsx */

import React from 'react';
import { jsx } from '@theme-ui/core';
import { Avatar as Component, AvatarProps as Props, ForwardRef } from '@theme-ui/components';

export interface AvatarProps extends Props {
  size?: 'small' | 'medium' | 'large'
}

export const Avatar: ForwardRef<HTMLImageElement, AvatarProps> = React.forwardRef(
  ({ size = 'large', ...rest }, ref) => {
    const tableSize = {
      small: 32,
      medium: 40,
      large: 64,
    }
    return (
      <Component
        ref={ref}
        width={tableSize[size]}
        height={tableSize[size]}
        {...rest} />
    )
  })