/** @jsx jsx */

import React from 'react';
import { jsx, useThemeUI } from '@theme-ui/core';
import { get } from '@theme-ui/css';
import { Heading as Component, HeadingProps as Props, ForwardRef } from '@theme-ui/components';

export interface HeadingProps extends Props {
  size: 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6'
}

export const Heading: ForwardRef<HTMLHeadingElement, HeadingProps> = React.forwardRef(
  ({ size = 'h2', children, ...rest }, ref) => {
    const context = useThemeUI();
    const sizes = get(context.theme, `text.heading.${size}`);
    return (
      <Component
        {...rest}
        ref={ref}
        sx={{ ...sizes }}
      >
        {children}
      </Component>
    );
  }
)

export default Heading;