/** @jsx jsx */

import { jsx, useThemeUI } from '@theme-ui/core';
import { get } from '@theme-ui/css';
import {
  Spinner as Component,
  SpinnerProps as Props,
} from '@theme-ui/components';
import React from 'react';

export interface SpinnerProps extends Props {
  size: 'lg' | 'md' | 'sm';
  variant: 'primary' | 'secondary';
}

export const Spinner: React.FC<SpinnerProps> = ({
  size = 'md',
  variant = 'primary',
  children,
  ...rest
}: SpinnerProps) => {
  const context = useThemeUI();
  const variants = get(context.theme, `spinner.${variant}`);
  return (
    <React.Fragment>
      {size === 'sm' && (
        <Component
          sx={{
            ...variants,
          }}
          strokeWidth={4}
          size={24}
          {...rest}
        />
      )}
      {size === 'md' && (
        <Component
          sx={{
            ...variants,
          }}
          {...rest}
        />
      )}
      {size === 'lg' && (
        <Component
          sx={{
            ...variants,
          }}
          strokeWidth={6}
          size={72}
          {...rest}
        />
      )}
    </React.Fragment>
  );
};

export default Spinner;
